import React, { useState } from 'react'
import "./Tab.css"
import data from './Data-table.json'
import { element } from 'prop-types';
import { nanoid } from 'nanoid' ;


export const Tab = () => {
    const [contacts, setContacts] = useState(data);
    const [addFormData , setAddFormData] = useState( {
        fullName : '' ,
        address : '' ,
        phoneNumber : '' ,
        email : ''
    }) ;

    const handleAddFormChange = (event) => {
        event.preventDefault();
        const fieldName= event.target.getAttribute('name') ;
        const fieldValue = event.target.value ;
        const newFormData = { ...addFormData} ;
        newFormData[fieldName] = fieldValue ;

        setAddFormData(newFormData)
    }


    const handleAddFormSubmit = (event) => {
        event.preventDefault() ;
        const newContact = {
            id : nanoid() ,
            fullName : addFormData.fullName ,
            address : addFormData.address ,
            phoneNumber: addFormData.phoneNumber ,
            email : addFormData.email
        } ;
    } ;
    return (
        <div className='table-container'>
            <table>
                <thead>
                    <tr>
                        <th>
                            Name
                        </th>
                        <th>
                            Adress
                        </th>
                        <th>
                            Phone number
                        </th>
                        <th>
                            Email
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {contacts.map((contact) => (
                        <tr>
                            <td>{contact.fullName}</td>
                            <td>{contact.address}</td>
                            <td>{contact.phoneNumber}</td>
                            <td>{contact.phoneNumber}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <h2>
                Add a contact
            </h2>
            <form>
                <input type="text" name="fullName" required="required" placeholder='Enter a name ...' onChange={handleAddFormChange}/>
                <input type="text" name="adress" required="required" placeholder='Enter an address ...' onChange={handleAddFormChange} />
                <input type="text" name="phoneNumber" required="required" placeholder='Enter a phone number ...' onChange={handleAddFormChange} />
                <input type="email" name="email" required="required" placeholder='Enter an email ...' onChange={handleAddFormChange} />
                <button type='submit'>Add</button>
            </form>
        </div>
    )
}

export default Tab;
